import "../styles/styleFolder.css";
import Close from "../assets/images/iconsWindowsXP/close.png";
import Hide from "../assets/images/iconsWindowsXP/hide.png";
import Big from "../assets/images/iconsWindowsXP/big.png";
import Pdf from "../assets/images/iconsWindowsXP/pdf.png";
import { useState } from "react";
import Draggable from "react-draggable";

export default function FolderPdf(props) {
	const [bigWindow, setBigWindow] = useState(true);
	function big_or_small() {
		if (bigWindow) {
			setBigWindow(false);
			document.querySelector(".Pdf").style.width = "100vw";
			document.querySelector(".Pdf").style.height = "96vh";
			// document.querySelector(".Pdf").style.border = "0px";
			document.querySelector(".Pdf").style.zIndex = "1";
			// document.querySelector(".about").style.top = "-2%";
			// document.querySelector(".about").style.left = "0%";
			// document.querySelector(".about").style.right = "0";
			// document.querySelector(".about").style.buttom = "0%";
		} else {
			setBigWindow(true);
			document.querySelector(".Pdf").style.width = "50vw";
			document.querySelector(".Pdf").style.height = "80vh";
			document.querySelector(".about").style.top = "0";
			document.querySelector(".Pdf").style.border = " 6px solid #0a25c0";
			document.querySelector(".Pdf").style.zIndex = "1";
			document.querySelector(".Pdf").style.border = " 6px solid #0a25c0";
			document.querySelector(".about").style.left = "2%";
		}
	}
	return (
		<Draggable>
			<div className="Pdf about">
				<div className="readmeTitle">
					<div className="NameIcon">
						<img src={Pdf} className="IconFolderRedme"></img>
						Cv_Mouad_Ajmani
					</div>
					<div className="closeIcons">
						<img src={Hide} className="closeIcon disable"></img>
						<img
							src={Big}
							onClick={big_or_small}
							onTouchEnd={big_or_small}
							className="closeIcon "
						></img>
						<img
							src={Close}
							onClick={() => props.setShowCV_fr(false)}
							onTouchEnd={() => props.setShowCV_fr(false)}
							className="closeIcon"
						></img>
					</div>
				</div>
				<div className="menuTextReadme">
					<ul>
						<li>File</li>
						<li>Edit</li>
						<li>View</li>
						<li>Favorites</li>
						<li>Tools</li>
						<li>Help</li>
					</ul>
				</div>

				<div className="SpaceText">
					`PROJECTS FT_TRANSCENDENCE • Developed an online ping-pong web
					application with a front-end built using vanilla JavaScript, Django
					for the back-end, and PostgreSQL for the database. • Contributed by
					designing and implementing the game mechanics on both the front-end
					and back-end. Utilized WebSockets to enable real-time interactions,
					enhancing the user experience. HTTP Web Server Contributed to the
					development of a custom web server, focusing on HTTP methods (GET,
					POST, DELETE) implementation. Applied expertise in sockets programming
					and multiplexing to enhance the server's efficiency, enabling
					concurrent handling of multiple client requests. • • INCEPTION
					Inception focuses on expanding my Docker expertise by configuring
					diverse services in separate containers. Using tools like Docker,
					docker-compose, and incorporating MariaDB, Nginx, and WordPress. the
					project aims to enhance my knowledge in containerization and
					orchestration, fostering a comprehensive understanding of deploying
					services within a Docker environment. • • • EDUCATION 10/2022 –
					present Benguerir, Maroc 1337 Coding School, Mohammed VI Polytechnic
					University Software Developer Relevant Coursework: Web Development,
					Algorithms, Unix, Network and System Administration, Object Oriented
					Programming, Graphics, Operating Systems, Data Structures • 09/2021 –
					05/2022 Rabat, Maroc PROFESSIONAL LICENSE IN PROFESSIONS DIGITAL
					COMMUNICATION FACULTY OF LETTERS AND HUMAN SCIENCES Relevant
					Coursework: Web Development, Design, Communication • 09/2019 – 07/2021
					Rabat, Maroc Specialized Technician Multimedia Development Ista Ntic,
					OFPPT Relevant Coursework: Algorithms , Web Development, Graphics,
					Design, UX/UI, Algorithms, Data Structures SKILLS Technical Skills::
					HTML, CSS,JavaScript, Django, C, C++, SQL, UX/UI,Figma, Docker, Adobe
					Photoshop, Adobe Illustrator CERTIFICATES FORMATION QUALIFIANTE EN
					LANGUAGE PYTHON. (ISTA) Programming with JavaScript (Meta) Programming
					in PYTHON(Meta)`
				</div>

				{/* <div className="readmeContent"></div> */}
			</div>
		</Draggable>
	);
}
