import React from "react";
import "../styles/loading.css";
import logo_windowsXp from "../assets/images/logo_windowsXp.png";

export default function Loading_windowsXp() {
    const [isVisible, setIsVisible] = useState(true);

  useEffect(() => {
    // Set a timer to hide the component after 6 seconds (6000 milliseconds)
    const timer = setTimeout(() => {
      setIsVisible(false);
    }, 6000);

    // Clear the timer if the component unmounts before 6 seconds
    return () => clearTimeout(timer);
  }, []);

  // If isVisible is false, don't render the component
  if (!isVisible) return null;
	return (
		<div className="fullLoading">
			<div className="logo_with_loading">
				<img
					src={logo_windowsXp}
					alt="logo_windowsXp"
					className="logo_windowsXp"
				/>
				<div className="loadingDiv">
					<div className="loadingBar"></div>
					<div className="loadingBar"></div>
					<div className="loadingBar"></div>
				</div>
			</div>
			<div className="footer">
				<p>Copyright Â© Microsoft Corporation </p>
				<strong>Microsoft</strong>
			</div>
		</div>
	);
}
