import React, { useState, useEffect } from "react";
import "../styles/loading.css";
import logo_windowsXp from "../assets/images/logo_windowsXp.png";
import LogPage from "./LogPage";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

export default function Loading_windowsXp() {
  const notify = () =>
    toast("Incorrect password. Please enter '1337'.", {
      position: "top-right",
      autoClose: 3000,
      hideProgressBar: false,
      closeOnClick: true,
      pauseOnHover: true,
      draggable: true,
      progress: undefined,
      theme: "white",
    });

  const [showHome, setShowHome] = useState(false);

  useEffect(() => {
    if (!showHome) {
      const timer = setTimeout(() => {
        notify(); // Call notify when the component mounts
        setShowHome(true); // Update showHome after the toast is triggered
        console.log("OOO");
      }, 5000);

      return () => clearTimeout(timer); // Clear the timeout on cleanup
    }
  }, [showHome]); // This runs when showHome is false

  return (
    <>
      <ToastContainer /> {/* This is required to display the toast */}
      {showHome ? (
        <LogPage />
      ) : (
        <div className="fullLoading">
          <div className="logo_with_loading">
            <img
              src={logo_windowsXp}
              alt="logo_windowsXp"
              className="logo_windowsXp"
            />
            <div className="loadingDiv">
              <div className="loadingBar"></div>
              <div className="loadingBar"></div>
              <div className="loadingBar"></div>
            </div>
          </div>
          <div className="footer">
            <p>Copyright Â© Microsoft Corporation</p>
            <strong>Microsoft</strong>
          </div>
        </div>
      )}
    </>
  );
}
